+<script lang="ts" setup>
+import { reactive, watch, ref, onMounted } from 'vue'
+import BoxItem from '../BoxItem/BoxItem.vue';
+import { useBox } from '../../store/box';
+
+const boxStore = useBox()
+let boxItemData = reactive(boxStore.boxItem)
+
+// 监听boxItemData
+watch(boxItemData, (nVal, oVal) => {
+  localStorage.setItem('boxItemData', JSON.stringify(boxItemData))
+})
+
+
+//如果没有从本地获取数据，那么从发送请求获取数据
+if (boxItemData == '') {
+}
+
+// 对item进行排序
+// 获取box元素
+const box: any = ref(null)
+// 定义box的宽高
+let BoxOffsetHeight
+let BoxOffsetWidth
+// 获取box的宽高
+onMounted(() => {
+  BoxOffsetHeight = box.value.offsetHeight
+  BoxOffsetWidth = box.value.offsetWidth
+  // 调用默认排序方法
+  // defaultSorting()
+})
+
+function defaultSorting() {
+  const interval = 100
+  let page = 0
+  let num = 0
+  boxItemData.forEach(element => {
+    let Y = num * 100
+    if (Y > BoxOffsetHeight - 100) {
+      page++
+      num = 0
+      Y = num * 100
+    }
+    let X = 100 * page
+    element.X = X
+    element.Y = Y
+    num++
+  });
+
+}
+// 右击菜单
+let visible = ref(false)
+let top = ref(0)
+let left = ref(0)
+watch(visible, (value) => {
+  if (value) {
+    document.body.addEventListener('click', closeMenu)
+  } else {
+    document.body.removeEventListener('click', closeMenu)
+  }
+})
+function openMenu(e) {
+  let x = e.pageX;
+  let y = e.pageY;
+  top.value = y;
+  left.value = x;
+  visible.value = true;
+}
+function closeMenu() {
+  visible.value = false;
+}
+function handlePaste(event) {
+  // 禁用鼠标右键
+  event.preventDefault()
+  return false
+}
+// 设置
+const drawer = ref(false)
+// 更新壁纸
+let bgColor = ref('pink')
+const updateWallpaper = () => {
+  bgColor.value = 'white'
+}
+</script>
+
+<template>
+
+  <div ref="box" class="box-grid" @contextmenu.prevent="openMenu($event)" :style="{ 'background-color': bgColor }">
+    <BoxItem v-for="(item, i) in boxItemData" :key="item.id" :w="item.w" :h="item.h" :i="i">
+    </BoxItem>
+  </div>
+  <!-- 右击菜单 -->
+  <el-card @contextmenu.native="handlePaste($event)" class="box-card contextmenu" v-show="visible"
+    :style="{ left: left + 'px', top: top + 'px' }">
+    <ul>
+      <li @click="defaultSorting()">默认排序</li>
+      <li @click="drawer = true">设置</li>
+      <li @click="updateWallpaper()">更新壁纸</li>
+    </ul>
+  </el-card>
+  <!-- 设置 -->
+  <el-drawer v-model="drawer" title="I am the title" :with-header="false">
+    <span>Hi there!</span>
+  </el-drawer>
+</template>
+<style lang="less">
+.box-grid {
+  overflow-x: hidden;
+  overflow-y: hidden;
+  height: 100%;
+  position: relative;
+
+}
+
+.contextmenu {
+  width: 150px;
+  height: 200px;
+  margin: 0;
+  background: #fff;
+  z-index: 3000;
+  position: absolute;
+  list-style-type: none;
+  padding: 5px 0;
+  border-radius: 4px;
+  font-size: 12px;
+  font-weight: 400;
+  color: #333;
+  box-shadow: 2px 2px 3px 0 rgba(0, 0, 0, 0.3);
+
+  .el-card__body {
+    padding: 0px;
+    line-height: 30px;
+
+    ul>li {
+      width: 100%;
+      height: 30px;
+      padding: 0 10px 0 10px;
+
+      &:hover {
+        background-color: pink;
+        cursor: pointer;
+      }
+    }
+  }
+}
+</style>
