+  let zIndex2: any = getComputedStyle(el).getPropertyValue('z-index');
+  zIndex2 = isNaN(zIndex2) ? 1 : zIndex2
+  zIndex = zIndex > zIndex2 ? Number(zIndex) + 1 : Number(zIndex2) + 1
+  setEleStyle({ "zIndex": zIndex, position: 'fixed', 'cursor': 'move' })
+  x = e.clientX - el.offsetLeft;
+  y = e.clientY - el.offsetTop;
+  window.document.onmousemove = (e) => onMouseMove(e);
+  window.document.onmouseup = (e) => onMouseUp(e)
+}
+//移动move
+function onMouseMove(e) {
+  let X = e.clientX - x
+  let Y = e.clientY - y;
+  if (X < 10 - el.offsetWidth) {
+    X = 10 - el.offsetWidth
+  } else if (X > document.documentElement.clientWidth - 10) {
+    X = document.documentElement.clientWidth - 10
+  }
+  if (Y < 10 - el.clientHeight) {
+    Y = 10 - el.clientHeight
+  } else if (Y > document.documentElement.clientHeight - 10) {
+    Y = document.documentElement.clientHeight - 10
+  }
+  el.style.left = X + 'px'
+  el.style.top = Y + 'px'
+
+}
+//释放
+function onMouseUp(e) {
+  document.onmousemove = null
+  document.onmouseup = null
+  setEleStyle({ 'cursor': 'pointer' })
+  el.setCapture && this.el.setCapture() //释放全局捕获
+
+}
+
+</script>
